<?php
// 产品详情页：USP 图标模块模板
if (function_exists('have_rows') && have_rows('usp_icons')) :
?>
    <section data-particle_enable="false" data-particle-mobile-disabled="false"
        class="elementor-section elementor-inner-section elementor-element elementor-section-boxed elementor-section-height-default elementor-section-height-default"
        data-element_type="section">
        <div class="elementor-container elementor-column-gap-default">
            <?php
            while (have_rows('usp_icons')) :
                the_row();
                $usp_title = function_exists('get_sub_field') ? get_sub_field('title') : '';
                $usp_description = function_exists('get_sub_field') ? get_sub_field('description') : '';
                $icon_type = function_exists('get_sub_field') ? get_sub_field('icon_type') : '';
                $icon_svg = function_exists('get_sub_field') ? get_sub_field('icon_svg') : '';
                $icon_image = function_exists('get_sub_field') ? get_sub_field('icon_image') : '';
                $icon_class = function_exists('get_sub_field') ? get_sub_field('icon_class') : '';
            ?>
                <div class="elementor-column elementor-col-33 elementor-inner-column elementor-element">
                    <div class="elementor-widget-wrap elementor-element-populated">
                        <div
                            class="elementor-element elementor-view-default elementor-position-top elementor-mobile-position-top elementor-widget elementor-widget-icon-box">
                            <div class="elementor-widget-container">
                                <div class="elementor-icon-box-wrapper">
                                    <div class="elementor-icon-box-icon">
                                        <span class="elementor-icon">
                                            <?php
                                            if ($icon_type === 'svg' && $icon_svg) {
                                                echo $icon_svg;
                                            } elseif ($icon_type === 'image' && $icon_image) {
                                                $icon_image_url = '';
                                                if (is_array($icon_image) && isset($icon_image['url'])) {
                                                    $icon_image_url = $icon_image['url'];
                                                } elseif ($icon_image) {
                                                    $icon_image_url = wp_get_attachment_image_url($icon_image, 'thumbnail');
                                                }
                                                if ($icon_image_url) {
                                            ?>
                                                    <img src="<?php echo esc_url($icon_image_url); ?>"
                                                        alt="<?php echo esc_attr($usp_title); ?>" />
                                                <?php
                                                }
                                            } elseif ($icon_class) {
                                                ?>
                                                <i class="<?php echo esc_attr($icon_class); ?>"></i>
                                            <?php
                                            }
                                            ?>
                                        </span>
                                    </div>
                                    <div class="elementor-icon-box-content">
                                        <?php if ($usp_title) { ?>
                                            <h4 class="elementor-icon-box-title">
                                                <span><?php echo esc_html($usp_title); ?></span>
                                            </h4>
                                        <?php } ?>
                                        <?php if ($usp_description) { ?>
                                            <p class="elementor-icon-box-description">
                                                <?php echo wp_kses_post($usp_description); ?>
                                            </p>
                                        <?php } ?>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            <?php endwhile; ?>
        </div>
    </section>
<?php
    return;
endif;
?>
